import { useState } from 'react';
import { Button, Container } from '@mui/material';
import LazyTable from '../components/LazyTable';
const config = require('../config.json');

export default function Top3Genres() {
  const [movies, setMovies] = useState([]);
  const [columns] = useState([
    { field: 'title', headerName: 'Movie Title' },
    { field: 'genre', headerName: 'Genre' },
    { field: 'average_rating', headerName: 'Average Rating' },
    { field: 'n_rating', headerName: 'Number of Ratings' },
  ]);

  const fetchTop3GenresMovies = () => {
    fetch(`http://${config.server_host}:${config.server_port}/top3genres?userID=Group15`) // Replace 'Group15' with the logged-in user's ID
      .then(res => res.json())
      .then(data => setMovies(data))
      .catch(err => console.error(err));
  };

  return (
    <Container>
      <h2>Top Movies in Your Favorite Genres</h2>
      <Button variant="contained" color="primary" onClick={fetchTop3GenresMovies}>
        Fetch Top 3 Genres Movies
      </Button>
      {movies.length > 0 && (
        <LazyTable
          route=""
          data={movies} // Pass the fetched data directly
          columns={columns}
        />
      )}
    </Container>
  );
}
