import { useEffect, useState } from 'react';
import { Container, Divider, Tab, Tabs, Typography, Grid, Card, CardMedia, CardContent } from '@mui/material';
const config = require('../config.json');

export default function HomePage() {
  const [topMovies, setTopMovies] = useState([]);
  const [genres, setGenres] = useState(['All', 'Action', 'Adventure', 'Animation', 'Children', 'Comedy', 'Crime', 'Documentary', 'Drama', 'Fantasy', 'Film-Noir', 'Horror', 'IMAX', 'Musical', 'Mystery', 'Romance', 'Sci-Fi', 'Thriller', 'War', 'Western']); // Example genres
  const [selectedGenre, setSelectedGenre] = useState('All');
  const [error, setError] = useState(null);

  // Fetch the top 10 movies or movies by genre
  const fetchMovies = (genre) => {
    const route =
      genre === 'All'
        ? `${config.server_host}:${config.server_port}/top10movies`
        : `${config.server_host}:${config.server_port}/top10bygenre/${genre}`;

    fetch(route)
      .then((res) => res.json())
      .then((data) => {
        setTopMovies(data);
        setError(null);
      })
      .catch((err) => {
        setError('Error fetching');
        console.error(err);
      });
  };

  // Fetch movies on component mount or when the selected genre changes
  useEffect(() => {
    fetchMovies(selectedGenre);
  }, [selectedGenre]);

  const handleGenreChange = (event, newValue) => {
    setSelectedGenre(newValue);
  };

  return (
    <Container>
      {/* Hero Section */}
      <Typography variant="h3" gutterBottom align="center" sx={{ mt: 4 }}>
        Discover Your Next Favorite Movie
      </Typography>
      <Typography variant="subtitle1" align="center" gutterBottom>
        Explore top-rated movies and personalized recommendations.
      </Typography>

      {/* Genre Tabs */}
      <Tabs
        value={selectedGenre}
        onChange={handleGenreChange}
        indicatorColor="primary"
        textColor="primary"
        variant="scrollable"
        scrollButtons="auto"
        sx={{ mt: 4 }}
      >
        {genres.map((genre) => (
          <Tab key={genre} label={genre} value={genre} />
        ))}
      </Tabs>

      {/* Top Movies Section */}
      <Typography variant="h4" gutterBottom sx={{ mt: 4 }}>
        Top 10 Movies {selectedGenre !== 'All' && `in ${selectedGenre}`}
      </Typography>
      <Divider sx={{ mb: 2 }} />
      <Grid container spacing={3}>
        {topMovies.length > 0 ? (
          topMovies.map((movie) => (
            <Grid item xs={12} sm={6} md={4} key={movie.movieID}>
              <Card>
                <CardMedia
                  component="img"
                  height="300"
                  image={movie.poster_link || '/placeholder.png'} // Placeholder if no poster link
                  alt={movie.title}
                />
                <CardContent>
                  <Typography variant="h6">{movie.title}</Typography>
                  <Typography variant="body2" color="text.secondary">
                    Rating: {movie.average_rating.toFixed(1)}
                  </Typography>
                </CardContent>
              </Card>
            </Grid>
          ))
        ) : (
          <Typography>{error || 'Loading top movies...'}</Typography>
        )}
      </Grid>
    </Container>
  );
}
